import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getCSRFToken, sendOTP, verifyOTP } from "../auth/AuthUtils";

const Login = () => {
  // We use "phone" here because the flow is phone-based.
  const [userInfo, setUserInfo] = useState({ phone: "", password: "" });
  const [otp, setOTP] = useState("");
  // otpStage can be "none", "sent", or "verified"
  const [otpStage, setOtpStage] = useState("none");
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo(prev => ({ ...prev, [name]: value }));
  };

  // Send OTP to the provided phone number
  const handleSendOTP = async () => {
    if (!userInfo.phone) {
      alert("Please enter your phone number.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/send-otp/",
        { phone_number: userInfo.phone },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        alert("OTP sent successfully!");
        setOtpStage("sent");
      } else {
        alert("Failed to send OTP: " + response.data.message);
      }
    } catch (error) {
      console.error("Error sending OTP:", error.response?.data || error);
      alert("Error sending OTP. Check console for details.");
    }
  };

  // Verify the OTP entered by the user
  const handleVerifyOTP = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }
    try {
      const response = await verifyOTP(userInfo.phone, otp);
      if (response.success) {
        alert("OTP verified successfully!");
        setOtpStage("verified");
      } else {
        alert("Invalid OTP.");
      }
    } catch (error) {
      console.error("Error verifying OTP:", error);
      alert("Error verifying OTP.");
    }
  };

  // Complete login after OTP verification
  const handleLogin = async () => {
    if (!userInfo.phone || !userInfo.password) {
      alert("Please enter both phone number and password.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login-otp/",
        {
          phone: userInfo.phone,
          password: userInfo.password,
          otp: otp,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        const userData = {
          name: response.data.user.name,
          phone: response.data.user.phone_number,
        };
        localStorage.setItem("authToken", response.data.authToken);
        localStorage.setItem("user", JSON.stringify(userData));
        window.dispatchEvent(new Event("storage"));
        alert("Login successful!");
        navigate("/profile");
      } else {
        alert("Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("Login Error:", error.response?.data || error);
      alert("Login failed. Check console for details.");
    }
  };

  // This handler drives the OTP flow:
  // - If no OTP sent, it sends OTP.
  // - If OTP sent but not verified, it verifies the OTP.
  // - If OTP is verified, it logs in.
  const handleButtonClick = async () => {
    if (otpStage === "none") {
      await handleSendOTP();
    } else if (otpStage === "sent") {
      await handleVerifyOTP();
    } else if (otpStage === "verified") {
      await handleLogin();
    }
  };

  let buttonText = "";
  if (otpStage === "none") {
    buttonText = "Send OTP";
  } else if (otpStage === "sent") {
    buttonText = "Verify OTP";
  } else if (otpStage === "verified") {
    buttonText = "Login";
  }

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        name="phone"
        placeholder="Enter your phone number"
        value={userInfo.phone}
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Enter your password"
        value={userInfo.password}
        onChange={handleChange}
      />
      {/* Only show the OTP input if we've already sent an OTP */}
      {otpStage !== "none" && (
        <input
          type="text"
          placeholder="Enter OTP"
          value={otp}
          onChange={(e) => setOTP(e.target.value)}
        />
      )}
      <button onClick={handleButtonClick}>{buttonText}</button>
      <button onClick={() => navigate("/signup")}>Signup</button>
      <button onClick={() => navigate("/forgot-password")}>
        Forgot Password?
      </button>
    </div>
  );
};

export default Login;
