import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { getCSRFToken } from '../auth/AuthUtils';  

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [addresses, setAddresses] = useState([]);  // âœ… Ensure initial state is an empty array
  const [selectedAddress, setSelectedAddress] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Load cart items from localStorage
    const storedCartItems = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(storedCartItems);

    // Calculate total price
    const total = storedCartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalPrice(total);

    // Fetch addresses from the database
    fetchAddresses();
  }, []);

  // âœ… Fetch Addresses from Database
  const fetchAddresses = async () => {
    console.log("ðŸ”„ Fetching addresses...");
    try {
      const csrfToken = await getCSRFToken();  
      const authToken = localStorage.getItem("authToken"); 

      const response = await axios.get("http://127.0.0.1:8000/api/auth/addresses/", {
        withCredentials: true,
        headers: {
          "X-CSRFToken": csrfToken,
          "Authorization": authToken ? `Token ${authToken}` : "",
          "Content-Type": "application/json",
        },
      });

      console.log("âœ… Address Data:", response.data);

      // âœ… Ensure `response.data` is an array before setting state
      setAddresses(Array.isArray(response.data) ? response.data : []);

      // âœ… Set default selected address if available
      if (Array.isArray(response.data) && response.data.length > 0) {
        setSelectedAddress(response.data[0].id);
      }
    } catch (error) {
      console.error("ðŸš¨ Error fetching addresses:", error.response?.data || error);
      alert("Error loading addresses. Please check authentication.");
      setAddresses([]);  // âœ… Set an empty array on error
    }
  };

  return (
    <div>
      <h1>Your Cart</h1>

      {/* Cart Items */}
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((item, index) => (
            <div key={index} className="cart-item" style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
              <img src={item.image || '/images/products/default.jpg'} alt={item.name || 'Product'} width="100" height="100" style={{ marginRight: '20px' }} />
              <div style={{ flexGrow: 1 }}>
                <h3>{item.name || `Product ${item.id}`}</h3>
                <p>Price: â‚¹{item.price}</p>
                <div>
                  <button>-</button>
                  <span style={{ margin: '0 10px' }}>{item.quantity}</span>
                  <button>+</button>
                </div>
                <p>Total: â‚¹{(item.price * item.quantity).toFixed(2)}</p>
              </div>
              <button className="btn btn-danger">Remove</button>
            </div>
          ))}
          <p><strong>Total Price: â‚¹{totalPrice.toFixed(2)}</strong></p>
          <button onClick={() => navigate(`/checkout?total_price=${totalPrice}&address_id=${selectedAddress}`)} className="btn btn-success">Checkout</button>
        </div>
      )}

      {/* Address Selection from Database */}
      <h2>Select Address for Delivery</h2>
      {addresses.length > 0 ? (
        <select
          value={selectedAddress || ''}
          onChange={(e) => setSelectedAddress(e.target.value)}
          id="address-select"
        >
          {addresses.map((address) => (
            <option key={address.id} value={address.id}>
              {address.address_line1}, {address.city}, {address.state} - {address.postal_code}
            </option>
          ))}
        </select>
      ) : (
        <p>No saved addresses. <button onClick={() => navigate("/add-address")}>Add Address</button></p>
      )}
    </div>
  );
};

export default Cart;
