import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/auth/login/', loginData, {
        withCredentials: true,
      });

      if (response.data.success) {
        alert('Login successful!');
        navigate('/profile');  // Redirect to profile page
      } else {
        alert('Invalid credentials');
      }
    } catch (error) {
      alert('Login failed. Check credentials and try again.');
    }
  };

  return (
    <div className="form-container">
      <h2>Login</h2>
      <input type="text" name="username" placeholder="Email or Phone" value={loginData.username} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={loginData.password} onChange={handleChange} required />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
