import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { getCSRFToken } from './AuthUtils';

const Login = () => {
  const [loginInfo, setLoginInfo] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginInfo({ ...loginInfo, [name]: value });
  };

  // Handle login submission
  const handleLogin = async (e) => {
    e.preventDefault();
    setError(''); // Clear previous errors

    try {
      const csrfToken = getCSRFToken();
      if (!csrfToken) {
        setError('CSRF token missing. Please refresh and try again.');
        return;
      }

      const response = await axios.post('http://127.0.0.1:8000/api/auth/login/', 
        loginInfo, 
        {
          headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': csrfToken,
          },
          withCredentials: true,
        }
      );

      if (response.data.success) {
        alert('Login successful!');
        localStorage.setItem('user', JSON.stringify(response.data.user)); // Save user data
        navigate('/products'); // Redirect to products page
      } else {
        setError('Invalid username or password');
      }
    } catch (err) {
      setError('Error during login. Please try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <p className="error">{error}</p>}

      <form onSubmit={handleLogin}>
        <label>Username or Email:</label>
        <input type="text" name="username" placeholder="Enter username or email" value={loginInfo.username} onChange={handleChange} required />

        <label>Password:</label>
        <input type="password" name="password" placeholder="Enter password" value={loginInfo.password} onChange={handleChange} required />

        <button type="submit">Log In</button>
      </form>

      <p>
        <Link to="/password-reset" className="btn btn-link">Forgot Password?</Link>
      </p>
      <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
    </div>
  );
};

export default Login;
