import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../assets/styles/style.css';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const location = useLocation();  // To access the search query from the URL

  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/products/')
      .then(response => response.json())
      .then(data => {
        setProducts(data);
        handleSearch(data);  // Apply the search filter when products are fetched
        applyFilters(data);

      })
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  useEffect(() => {
    handleSearch(products); 
    applyFilters(products); // Re-run the filter when the search query changes
  }, [location.search]);

  const handleSearch = (allProducts) => {
    const queryParams = new URLSearchParams(location.search);
    const searchQuery = queryParams.get('search')?.toLowerCase() || '';

    if (searchQuery) {
      // Filter products based on search query
      const matchingProducts = allProducts.filter(product =>
        product.name.toLowerCase().includes(searchQuery) ||
        product.description.toLowerCase().includes(searchQuery) ||
        product.brand.toLowerCase().includes(searchQuery)
      );

      // Combine matching products first, followed by non-matching ones
      const nonMatchingProducts = allProducts.filter(product =>
        !matchingProducts.includes(product)
      );

      setFilteredProducts([...matchingProducts, ...nonMatchingProducts]);
    } else {
      // If no search query, display all products
      setFilteredProducts(allProducts);
    }
  };



    // ✅ Function to filter products by category & search query
    const applyFilters = (allProducts) => {
      const queryParams = new URLSearchParams(location.search);
      const searchQuery = queryParams.get('search')?.toLowerCase() || '';
      const selectedCategory = queryParams.get('category'); // ✅ Get selected category from URL

      let filtered = [...allProducts];

      // ✅ Filter by category (prioritize category products)
      let categoryFiltered = [];
      if (selectedCategory) {
        categoryFiltered = filtered.filter(product => product.category_name === selectedCategory);
        filtered = filtered.filter(product => product.category_name !== selectedCategory);
      }

      // ✅ Apply search filter within category & all products
      let matchingProducts = [];
      if (searchQuery) {
        matchingProducts = [...categoryFiltered, ...filtered].filter(product =>
          product.name.toLowerCase().includes(searchQuery) ||
          product.description.toLowerCase().includes(searchQuery) ||
          product.brand.toLowerCase().includes(searchQuery)
        );
        filtered = filtered.filter(product => !matchingProducts.includes(product));
      } else {
        matchingProducts = [...categoryFiltered];
      }

      // ✅ Combine results: Category products first → Matching products → Other products
      setFilteredProducts([...matchingProducts, ...filtered]);
    };


  return (
    <div>
      <h1>Our Products</h1>

      {/* Display a message if no matching products are found */}
      {filteredProducts.length > 0 && (
        <>
          <ul className="products-container">
            {filteredProducts.map((product, index) => (
              <li key={product.id} className={`product-box ${index === 0 ? 'highlight' : ''}`}>
                <img
                  src={`/images/products/${product.image.split('/').pop()}`}
                  alt={product.name}
                  className="product-image"
                />
                <h2>{product.name}</h2>
                <p>{product.description}</p>
                <p><strong>Price:</strong> ₹{product.price}</p>
                <button onClick={() => window.addToCart(product.id, product.name, product.price, product.image)}>
                  Add to Cart
                </button>
              </li>
            ))}
          </ul>
        </>
      )}

      {/* Fallback when no matches are found */}
      {filteredProducts.length === 0 && (
        <p>No matching products found. Showing all available products below:</p>
      )}
    </div>
  );
};

export default Products;
