import React, { useEffect } from 'react';

// Helper function to dynamically import images
const importAll = (requireContext) => {
  const images = requireContext.keys().map(requireContext);
  console.log('Imported Images:', images);  // Log to debug image paths
  return images;
};

// Import images for slides and categories
const slideImages = importAll(require.context('../assets/images/slides', false, /\.(png|jpe?g|jpeg|svg)$/));
const categoryImages = importAll(require.context('../assets/images/categories', false, /\.(png|jpe?g|jpeg|svg)$/));

const Home = () => {
  useEffect(() => {
    // Log imported images to verify correct paths
    slideImages.forEach((image, index) => console.log(`Slide Image ${index + 1}:`, image.default || image));
    categoryImages.forEach((image, index) => console.log(`Category Image ${index + 1}:`, image.default || image));
  }, []);

  return (
    <div className="content-wrapper">
      {/* Hero Section */}
      <section id="hero">
        <div id="heroCarousel" className="carousel slide" data-bs-ride="carousel" data-bs-interval="2000">
          <div className="carousel-inner">
            {slideImages.map((image, index) => (
              <div className={`carousel-item ${index === 0 ? 'active' : ''}`} key={index}>
                <img
                  src={image.default || image}  // Webpack compatibility
                  className="d-block w-100"
                  alt={`Slide ${index + 1}`}
                  style={{ maxHeight: '400px', objectFit: 'cover' }}  // Ensure images fit nicely
                />
                <div className="carousel-caption d-none d-md-block">
                  <h1>Revolutionizing the Green Industry</h1>
                  <p>We deliver the freshest, eco-friendly food at your doorstep while nurturing the planet.</p>
                </div>
              </div>
            ))}
          </div>

          {/* Carousel Controls */}
          <button className="carousel-control-prev" type="button" data-bs-target="#heroCarousel" data-bs-slide="prev">
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Previous</span>
          </button>
          <button className="carousel-control-next" type="button" data-bs-target="#heroCarousel" data-bs-slide="next">
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="visually-hidden">Next</span>
          </button>
        </div>
      </section>

      {/* Categories Section */}
      <section id="categories" style={{ marginTop: '50px' }}>
        <div className="container">
          <h2>Our Product Categories</h2>
          <div className="category-container" style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
            {categoryImages.map((image, index) => (
              <div className="category-box" key={index} style={{ textAlign: 'center' }}>
                <a href={`/products/${index}`}>
                  <img
                    src={image.default || image}  // Webpack compatibility
                    alt={`Category ${index + 1}`}
                    className="category-image"
                    style={{
                      width: '150px',
                      height: '150px',
                      objectFit: 'cover',
                      borderRadius: '10px',
                    }}
                  />
                </a>
                <p style={{ marginTop: '10px' }}>Category {index + 1}</p>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
