import React, { useEffect, useState } from "react";

const Home = () => {
  const [categories, setCategories] = useState([]); // ✅ Add state for categories
  const [selectedCategory, setSelectedCategory] = useState(null); // ✅ Track selected category
  const [products, setProducts] = useState([]); // ✅ Store filtered products

  // ✅ Fetch categories from API
  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/categories/", {
      credentials: "include", // ✅ Ensures cookies are sent
    })
      .then((response) => {
        if (!response.ok) throw new Error("Failed to fetch categories");
        return response.json();
      })
      .then((data) => {
        console.log("✅ Fetched Categories:", data);
        setCategories(data); // ✅ Save categories in state
      })
      .catch((error) => console.error("❌ Error fetching categories:", error));
  }, []);

  // ✅ Handle category selection to filter products
  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setProducts(category.products); // ✅ Filter products based on selected category
  };

  return (
    <div className="container">
      <h2>Our Product Categories</h2>
      <div className="category-container" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {categories.map((category) => (
          <div key={category.id} className="category-box" onClick={() => handleCategoryClick(category)}>
            <img
              src={`http://127.0.0.1:8000/static/categories/${category.name.toLowerCase()}.jpg`} // ✅ Match image with category name
              alt={category.name}
              className="category-image"
              style={{ width: "150px", height: "150px", objectFit: "cover", borderRadius: "10px" }}
            />
            <p>{category.name}</p>
          </div>
        ))}
      </div>

      {/* ✅ Show Products Only When a Category is Selected */}
      {selectedCategory && (
        <div>
          <h3>Products in {selectedCategory.name}</h3>
          <div className="product-container" style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
            {products.length > 0 ? (
              products.map((product) => (
                <div key={product.id} className="product-box">
                  <img
                    src={product.image}
                    alt={product.name}
                    className="product-image"
                    style={{ width: "150px", height: "150px", objectFit: "cover", borderRadius: "10px" }}
                  />
                  <p>{product.name}</p>
                  <p>₹{product.price}</p>
                </div>
              ))
            ) : (
              <p>No products available in this category.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
