import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [addresses, setAddresses] = useState([]);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [newAddress, setNewAddress] = useState({
    name: '',
    address_line1: '',
    city: '',
    postal_code: '',
  });

  const navigate = useNavigate();

  useEffect(() => {
    // Load cart items from localStorage
    const storedCartItems = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(storedCartItems);

    // Calculate total price
    const total = storedCartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalPrice(total);

    // Load addresses from localStorage or mock data

    setAddresses(storedAddresses);

    // Set default selected address
    if (storedAddresses.length > 0) {
      setSelectedAddress(storedAddresses[0].id);
    }
  }, []);

  // Update cart items in localStorage
  const updateCart = (updatedCart) => {
    setCartItems(updatedCart);
    localStorage.setItem('cart', JSON.stringify(updatedCart));
    const total = updatedCart.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalPrice(total);
  };

  // Increase item quantity
  const increaseQuantity = (index) => {
    const updatedCart = [...cartItems];
    updatedCart[index].quantity += 1;
    updateCart(updatedCart);
  };

  // Decrease item quantity
  const decreaseQuantity = (index) => {
    const updatedCart = [...cartItems];
    if (updatedCart[index].quantity > 1) {
      updatedCart[index].quantity -= 1;
    } else {
      updatedCart.splice(index, 1);
    }
    updateCart(updatedCart);
  };

  // Remove item from cart
  const removeItem = (index) => {
    const updatedCart = [...cartItems];
    updatedCart.splice(index, 1);
    updateCart(updatedCart);
  };

  // Handle checkout
  const handleCheckout = () => {
    if (!selectedAddress) {
      alert("Please select an address before proceeding to checkout.");
      return;
    }
    navigate(`/checkout?total_price=${totalPrice.toFixed(2)}&address_id=${selectedAddress}`);
  };

  // Handle new address input changes
  const handleAddressChange = (e) => {
    const { name, value } = e.target;
    setNewAddress((prevAddress) => ({
      ...prevAddress,
      [name]: value,
    }));
  };

  // Add new address
  const addAddress = () => {
    if (!newAddress.name || !newAddress.address_line1 || !newAddress.city || !newAddress.postal_code) {
      alert("Please fill all address fields.");
      return;
    }

    const updatedAddresses = [...addresses, { ...newAddress, id: Date.now() }];
    setAddresses(updatedAddresses);
    localStorage.setItem('addresses', JSON.stringify(updatedAddresses));
    setNewAddress({ name: '', address_line1: '', city: '', postal_code: '' });
    alert("Address added successfully!");
  };

  // Edit existing address
  const editAddress = (id) => {
    const updatedAddresses = addresses.map((address) =>
      address.id === id ? { ...address, ...newAddress } : address
    );
    setAddresses(updatedAddresses);
    localStorage.setItem('addresses', JSON.stringify(updatedAddresses));
    setNewAddress({ name: '', address_line1: '', city: '', postal_code: '' });
    alert("Address updated successfully!");
  };

  return (
    <div>
      <h1>Your Cart</h1>

      {/* Cart Items */}
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((item, index) => (
            <div key={index} className="cart-item" style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
              <img src={item.image || '/images/products/default.jpg'} alt={item.name || 'Product'} width="100" height="100" style={{ marginRight: '20px' }} />
              <div style={{ flexGrow: 1 }}>
                <h3>{item.name || `Product ${item.id}`}</h3>
                <p>Price: ₹{item.price}</p>

                <div>
                  <button onClick={() => decreaseQuantity(index)}>-</button>
                  <span style={{ margin: '0 10px' }}>{item.quantity}</span>
                  <button onClick={() => increaseQuantity(index)}>+</button>
                </div>

                <p>Total: ₹{(item.price * item.quantity).toFixed(2)}</p>
              </div>

              <button onClick={() => removeItem(index)} className="btn btn-danger">Remove</button>
            </div>
          ))}

          <p><strong>Total Price: ₹{totalPrice.toFixed(2)}</strong></p>
          <button onClick={handleCheckout} className="btn btn-success">Checkout</button>
        </div>
      )}

      {/* Address Selection */}
      <h2>Select Address for Delivery</h2>
      {addresses.length > 0 ? (
        <select
          value={selectedAddress || ''}
          onChange={(e) => setSelectedAddress(e.target.value)}
          id="address-select"
        >
          {addresses.map((address) => (
            <option key={address.id} value={address.id}>
              {address.name}, {address.address_line1}, {address.city} - {address.postal_code}
            </option>
          ))}
        </select>
      ) : (
        <p>No saved addresses. Add an address below to proceed.</p>
      )}

      {/* Add/Edit Address Form */}
      <h3>{newAddress.id ? "Edit Address" : "Add New Address"}</h3>
      <form onSubmit={(e) => e.preventDefault()}>
        <input
          type="text"
          name="name"
          placeholder="Address Name (e.g., Home, Work)"
          value={newAddress.name}
          onChange={handleAddressChange}
        />
        <input
          type="text"
          name="address_line1"
          placeholder="Address Line 1"
          value={newAddress.address_line1}
          onChange={handleAddressChange}
        />
        <input
          type="text"
          name="city"
          placeholder="City"
          value={newAddress.city}
          onChange={handleAddressChange}
        />
        <input
          type="text"
          name="postal_code"
          placeholder="Postal Code"
          value={newAddress.postal_code}
          onChange={handleAddressChange}
        />
        <button onClick={newAddress.id ? () => editAddress(newAddress.id) : addAddress}>
          {newAddress.id ? "Update Address" : "Add Address"}
        </button>
      </form>
    </div>
  );
};

export default Cart;
