import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { getCSRFToken } from '../auth/AuthUtils';

const Login = () => {
  const [credentials, setCredentials] = useState({ email: '', password: '' });
  const navigate = useNavigate();
  const [userInfo, setUserInfo] = useState({ email: '', password: '' });


  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleLogin = async () => {
    console.log('üöÄ Attempting login...');

    try {
      const csrfToken = getCSRFToken();
      console.log('‚úÖ CSRF Token:', csrfToken);  // Debugging CSRF token

      const response = await axios.post(
        'http://127.0.0.1:8000/api/auth/login/',
        {
          email: userInfo.email,
          password: userInfo.password,
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': csrfToken,  // ‚úÖ Include CSRF Token
          },
          withCredentials: true, // ‚úÖ Important for session-based authentication
        }
      );

      console.log('üéâ Login Success:', response.data);
      if (response.data.success) {
        alert('‚úÖ Login successful!');
        navigate('/profile'); // Redirect to profile after login
      } else {
        alert('‚ùå Login failed: ' + response.data.message);
      }
    } catch (error) {
      console.error('üî• Login Error:', error.response?.data || error);
      alert('‚ùå Login failed. Check console for details.');
    }
  };


  return (
    <div className="login-container">
      <h2>Login</h2>
      <input type="email" name="email" placeholder="Email" value={credentials.email} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={credentials.password} onChange={handleChange} required />

      <button onClick={handleLogin}>Log In</button>

      {/* ‚úÖ Forgot Password & Signup Buttons */}
      <div className="extra-options">
        <button onClick={() => navigate("/forgot-password")} className="link-btn">Forgot Password?</button>
        <button onClick={() => navigate("/signup")} className="link-btn">Sign Up</button>
      </div>
    </div>
  );
};

export default Login;
