import React, { useEffect, useState } from "react";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      console.log("üîÑ Fetching profile...");

      // Get CSRF Token first (if required)
      const csrfResponse = await axios.get("http://127.0.0.1:8000/api/auth/csrf/", {
        withCredentials: true,  // ‚úÖ Ensures cookies are sent
      });

      const csrfToken = csrfResponse.data.csrfToken;
      console.log("‚úÖ CSRF Token:", csrfToken);

      // Fetch the user profile
      const response = await axios.get("http://127.0.0.1:8000/api/auth/profile/", {
        withCredentials: true, // ‚úÖ Ensures cookies are included
        headers: {
          "X-CSRFToken": csrfToken,
        },
      });

      console.log("‚úÖ Profile fetched:", response.data);
      setUser(response.data.user);
      setOrders(response.data.orders || []);
    } catch (error) {
      console.error("üö® Error fetching profile:", error.response?.data || error);
      alert("‚ùå Failed to load profile. Please log in again.");
    }
  };

  if (!user) {
    return <p>Loading profile...</p>;
  }

  return (
    <div>
      <h2>Profile</h2>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Phone:</strong> {user.phone_number}</p>

      <h3>Order History</h3>
      {orders.length > 0 ? (
        <ul>
          {orders.map((order) => (
            <li key={order.id}>
              Order #{order.id} - ‚Çπ{order.total_amount} - {order.status}
            </li>
          ))}
        </ul>
      ) : (
        <p>No orders found.</p>
      )}
    </div>
  );
};

export default Profile;
