import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    console.log("ðŸ”„ Fetching profile...");

    try {
      const csrfToken = getCSRFToken();
      console.log("âœ… CSRF Token:", csrfToken);

      const response = await axios.get("http://127.0.0.1:8000/api/auth/profile/", {
        headers: {
          "X-CSRFToken": csrfToken,
        },
        withCredentials: true,  // âœ… Ensures cookies are sent
      });

      console.log("âœ… Profile Data:", response.data);
      setProfile(response.data);
    } catch (error) {
      console.error("ðŸš¨ Error fetching profile:", error.response?.data || error);
      alert("Error loading profile. Redirecting to login...");
      navigate("/login");  // Redirect to login if unauthorized
    }
  };

  return (
    <div>
      <h1>User Profile</h1>
      {profile ? (
        <div>
          <p><strong>Name:</strong> {profile.name}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Phone:</strong> {profile.phone_number}</p>

          <h2>Orders</h2>
          {profile.orders.length > 0 ? (
            <ul>
              {profile.orders.map((order) => (
                <li key={order.id}>
                  <p><strong>Order ID:</strong> {order.id}</p>
                  <p><strong>Amount:</strong> â‚¹{order.total_amount}</p>
                  <p><strong>Status:</strong> {order.status}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No orders found.</p>
          )}
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
    </div>
  );
};

export default Profile;

// âœ… Function to Get CSRF Token from Cookies
const getCSRFToken = () => {
  const cookies = document.cookie.split(";");
  for (let cookie of cookies) {
    const [name, value] = cookie.trim().split("=");
    if (name === "csrftoken") return value;
  }
  return null;
};
