import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getCSRFToken } from '../auth/AuthUtils';

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const [addresses, setAddresses] = useState([]);

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchUserProfile();
    fetchCartItems();
  }, [location]);

  // ‚úÖ Fetch user details and addresses from API
  const fetchUserProfile = async () => {
    try {
      const csrfToken = await getCSRFToken();
      const authToken = localStorage.getItem("authToken");

      const response = await axios.get("http://127.0.0.1:8000/api/auth/profile/", {
        headers: {
          "X-CSRFToken": csrfToken,
          "Authorization": authToken ? `Token ${authToken}` : "",
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      console.log("‚úÖ User Profile:", response.data);
      setUserInfo(response.data);
      setAddresses(response.data.addresses || []);

      // ‚úÖ Set the selected address from the cart
      const queryParams = new URLSearchParams(location.search);
      const addressId = queryParams.get('address_id');
      if (addressId) {
        const selectedAddr = response.data.addresses.find(addr => addr.id.toString() === addressId);
        setSelectedAddress(selectedAddr || null);
      }
    } catch (error) {
      console.error("üö® Error fetching user profile:", error.response?.data || error);
      alert("‚ùå Failed to load profile. Redirecting to login...");
      navigate("/login");
    }
  };

  // ‚úÖ Load cart items
  const fetchCartItems = () => {
    const storedCartItems = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(storedCartItems);

    const total = storedCartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalPrice(total);
  };

  // ‚úÖ Handle final order submission
  const handlePlaceOrder = () => {
    if (!selectedAddress) {
      alert("‚ùå Please select a delivery address before proceeding.");
      return;
    }
    alert('‚úÖ Order placed successfully!');
    localStorage.removeItem('cart');  
    navigate('/');  
  };

  return (
    <div>
      <h1>Checkout</h1>

      {/* ‚úÖ Display User Information */}
      <div style={styles.section}>
        <h2>User Information</h2>
        {userInfo ? (
          <>
            <p><strong>Name:</strong> {userInfo.name}</p>
            <p><strong>Phone:</strong> {userInfo.phone_number}</p>
            <p><strong>Email:</strong> {userInfo.email}</p>
          </>
        ) : (
          <p>Loading user details...</p>
        )}
      </div>

      {/* ‚úÖ Delivery Address Selection */}
      <div style={styles.section}>
        <h2>Delivery Address</h2>
        {addresses.length > 0 ? (
          <ul style={styles.addressList}>
            {addresses.map((address) => (
              <li key={address.id} style={{ ...styles.addressItem, backgroundColor: selectedAddress?.id === address.id ? '#d4edda' : 'white' }}>
                <input
                  type="radio"
                  name="address"
                  value={address.id}
                  checked={selectedAddress?.id === address.id}
                  onChange={() => setSelectedAddress(address)}
                />
                <strong>{address.address_line1}</strong>, {address.city} - {address.postal_code}
              </li>
            ))}
          </ul>
        ) : (
          <p>No addresses found. <a href="/cart">Go back to cart</a> and add an address.</p>
        )}
      </div>

      {/* ‚úÖ Order Summary */}
      <div style={styles.section}>
        <h2>Order Summary</h2>
        {cartItems.length > 0 ? (
          <ul style={styles.cartList}>
            {cartItems.map((item, index) => (
              <li key={index} style={styles.cartItem}>
                <img src={item.image || "/images/products/default.jpg"} alt={item.name} style={styles.productImage} />
                <div>
                  <p><strong>{item.name}</strong></p>
                  <p>Price: ‚Çπ{item.price}</p>
                  <p>Quantity: {item.quantity}</p>
                  <p>Total: ‚Çπ{(item.price * item.quantity).toFixed(2)}</p>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>

      {/* ‚úÖ Total Price and Place Order Button */}
      <div style={styles.section}>
        <h2>Total Price: ‚Çπ{totalPrice.toFixed(2)}</h2>
        <button onClick={handlePlaceOrder} style={styles.placeOrderButton}>Place Order</button>
      </div>
    </div>
  );
};

export default Checkout;

const styles = {
  section: {
    marginBottom: '30px',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
  },
  addressList: {
    listStyle: 'none',
    padding: 0,
  },
  addressItem: {
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    marginBottom: '10px',
    cursor: 'pointer',
  },
  cartList: {
    listStyle: 'none',
    padding: 0,
  },
  cartItem: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
  },
  productImage: {
    width: '80px',
    height: '80px',
    objectFit: 'cover',
    marginRight: '20px',
    borderRadius: '5px',
  },
  placeOrderButton: {
    padding: '12px 20px',
    backgroundColor: '#28a745',
    color: 'white',
    fontSize: '16px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};
