import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);
  const [addresses, setAddresses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUserProfile();
    fetchUserOrders();
    fetchUserAddresses();
  }, []);

  // ✅ Fetch User Profile
  const fetchUserProfile = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/auth/profile/', { withCredentials: true });
      setUser(response.data);
    } catch (error) {
      console.error("Error fetching profile:", error);
      alert("Failed to load profile");
    }
  };

  // ✅ Fetch User Orders
  const fetchUserOrders = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/orders/', { withCredentials: true });
      setOrders(response.data);
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  // ✅ Fetch User Addresses
  const fetchUserAddresses = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/api/addresses/', { withCredentials: true });
      setAddresses(response.data);
    } catch (error) {
      console.error("Error fetching addresses:", error);
    }
  };

  return (
    <div className="profile-container">
      <h1>My Profile</h1>

      {/* ✅ Display User Details */}
      {user ? (
        <div className="user-details">
          <h2>Welcome, {user.name}</h2>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone_number}</p>
        </div>
      ) : (
        <p>Loading user details...</p>
      )}

      {/* ✅ Display Address Details */}
      <h2>My Addresses</h2>
      {addresses.length > 0 ? (
        <ul>
          {addresses.map((address) => (
            <li key={address.id}>
              <p><strong>{address.name}</strong></p>
              <p>{address.address_line1}, {address.city}, {address.state} - {address.postal_code}</p>
              <p><strong>Phone:</strong> {address.phone_number}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No addresses saved.</p>
      )}

      {/* ✅ Display User Orders */}
      <h2>My Orders</h2>
      {orders.length > 0 ? (
        <ul className="orders-list">
          {orders.map((order) => (
            <li key={order.id} className="order-box">
              <p><strong>Order ID:</strong> {order.id}</p>
              <p><strong>Status:</strong> {order.status}</p>
              <p><strong>Order Date:</strong> {new Date(order.order_date).toLocaleDateString()}</p>
              <p><strong>Total Amount:</strong> ₹{order.total_amount}</p>

              {/* ✅ Order Items */}
              <h3>Items:</h3>
              <ul>
                {order.items.map((item) => (
                  <li key={item.product.id}>
                    <p>{item.product.name} - {item.quantity} x ₹{item.price_at_order_time}</p>
                  </li>
                ))}
              </ul>

              {/* ✅ Order Address */}
              {order.address && (
                <div>
                  <h3>Delivery Address:</h3>
                  <p>{order.address.address_line1}, {order.address.city}, {order.address.state} - {order.address.postal_code}</p>
                </div>
              )}
            </li>
          ))}
        </ul>
      ) : (
        <p>No orders placed yet.</p>
      )}

      <button onClick={() => navigate('/products')}>Shop More</button>
    </div>
  );
};

export default Profile;
