import axios from 'axios';


// Function to get CSRF token from cookies
const getCSRFToken = () => {
    const name = 'csrftoken';
    const value = document.cookie.match('(^|;)\\s*' + name + '=([^;]*)');
    return value ? value.pop() : '';
  };

// Function to send OTP via Twilio WhatsApp API
// Function to send OTP
export function sendOTP(phone_number) {
  const csrfToken = getCSRFToken();  // Get CSRF token

  return axios.post('/api/auth/send_otp/', { phone_number }, {
    headers: {
      'Content-Type': 'application/json',
      'X-CSRFToken': csrfToken  // Include CSRF token in headers
    },
    withCredentials: true  // Important to include cookies in request
  })
  .then(response => response.data)
  .catch(error => {
    console.error('Error sending OTP:', error);
    throw error;
  });
}

// Function to verify OTP
export const verifyOTP = async (phoneNumber, otp) => {
  try {
    const response = await axios.post('/api/auth/verify_otp/', { phone_number: phoneNumber, otp });
    return response.data;
  } catch (error) {
    console.error('Error verifying OTP:', error);
    throw error;
  }
};
