import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [userInfo, setUserInfo] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchCartItems();
    fetchUserProfile();
  }, [location]);

  // âœ… Fetch Cart Items from Local Storage
  const fetchCartItems = () => {
    const storedCartItems = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(storedCartItems);
    const total = storedCartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalPrice(total);
  };

  // âœ… Fetch User Profile from API
  const fetchUserProfile = async () => {
    try {
      const csrfToken = await getCSRFToken();
      const authToken = localStorage.getItem("authToken");

      const response = await axios.get("http://127.0.0.1:8000/api/auth/profile/", {
        headers: {
          "X-CSRFToken": csrfToken,
          "Authorization": authToken ? `Token ${authToken}` : "",
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      setUserInfo({
        name: response.data.name,
        phone: response.data.phone_number,
        email: response.data.email,
      });

      // âœ… Get Selected Address from API
      const queryParams = new URLSearchParams(location.search);
      const addressId = queryParams.get("address_id");

      if (addressId) {
        const selectedAddr = response.data.addresses.find(addr => addr.id.toString() === addressId);
        setSelectedAddress(selectedAddr);
      } else if (response.data.addresses.length > 0) {
        setSelectedAddress(response.data.addresses[0]); // âœ… Default to first address
      }
    } catch (error) {
      console.error("ðŸš¨ Error fetching user profile:", error.response?.data || error);
      alert("Error loading profile. Redirecting to login...");
      navigate("/login");
    }
  };

  // âœ… Handle Order Placement
  const handlePlaceOrder = () => {
    alert("Order placed successfully!");
    localStorage.removeItem("cart"); // Clear cart after placing order
    navigate("/");
  };

  // âœ… Handle Razorpay Payment
  const handlePayment = async () => {
    try {
      const response = await axios.post("/api/create_razorpay_order/", { amount: totalPrice });
      const { order_id, key, amount } = response.data;

      const options = {
        key: key,
        amount: amount,
        currency: "INR",
        name: "Harit Aahar",
        description: "Payment for your order",
        order_id: order_id,
        handler: async function (response) {
          const { razorpay_payment_id, razorpay_order_id, razorpay_signature } = response;

          try {
            const verifyResponse = await axios.post("/api/verify_payment/", {
              razorpay_payment_id,
              razorpay_order_id,
              razorpay_signature,
            });

            if (verifyResponse.data.status === "success") {
              alert("Payment successful and verified!");
              localStorage.removeItem("cart");
              navigate("/");
            } else {
              alert("Payment verification failed. Please contact support.");
            }
          } catch (error) {
            console.error("Error verifying payment:", error);
            alert("An error occurred during payment verification.");
          }
        },
        prefill: {
          name: userInfo?.name || "",
          email: userInfo?.email || "",
          contact: userInfo?.phone || "",
        },
        notes: {
          address: selectedAddress
            ? `${selectedAddress.address_line1}, ${selectedAddress.city}`
            : "",
        },
        theme: {
          color: "#28a745",
        },
      };

      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error("Error during payment:", error);
      alert("Payment initiation failed. Please try again.");
    }
  };

  return (
    <div>
      <h1>Checkout</h1>

      {/* âœ… User Information */}
      <div style={styles.section}>
        <h2>User Information</h2>
        {userInfo ? (
          <>
            <p><strong>Name:</strong> {userInfo.name}</p>
            <p><strong>Phone:</strong> {userInfo.phone}</p>
            <p><strong>Email:</strong> {userInfo.email}</p>
          </>
        ) : (
          <p>Loading user details...</p>
        )}
      </div>

      {/* âœ… Delivery Address */}
      <div style={styles.section}>
        <h2>Delivery Address</h2>
        {selectedAddress ? (
          <div>
            <p><strong>{selectedAddress.name}</strong></p>
            <p>{selectedAddress.address_line1}</p>
            <p>{selectedAddress.city} - {selectedAddress.postal_code}</p>
          </div>
        ) : (
          <p>No address selected. <a href="/cart">Go back to cart</a> and select an address.</p>
        )}
      </div>

      {/* âœ… Order Summary */}
      <div style={styles.section}>
        <h2>Order Summary</h2>
        {cartItems.length > 0 ? (
          <ul style={styles.cartList}>
            {cartItems.map((item, index) => (
              <li key={index} style={styles.cartItem}>
                <img src={item.image} alt={item.name} style={styles.productImage} />
                <div>
                  <p><strong>{item.name}</strong></p>
                  <p>Price: â‚¹{item.price}</p>
                  <p>Quantity: {item.quantity}</p>
                  <p>Total: â‚¹{(item.price * item.quantity).toFixed(2)}</p>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>

      {/* âœ… Total Price & Place Order */}
      <div style={styles.section}>
        <h2>Total Price: â‚¹{totalPrice.toFixed(2)}</h2>
        <button onClick={handlePayment} style={styles.placeOrderButton}>Place Order</button>
      </div>
    </div>
  );
};

export default Checkout;

const styles = {
  section: {
    marginBottom: "30px",
    padding: "20px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    backgroundColor: "#f9f9f9",
  },
  cartList: {
    listStyle: "none",
    padding: 0,
  },
  cartItem: {
    display: "flex",
    alignItems: "center",
    marginBottom: "15px",
  },
  productImage: {
    width: "80px",
    height: "80px",
    objectFit: "cover",
    marginRight: "20px",
    borderRadius: "5px",
  },
  placeOrderButton: {
    padding: "12px 20px",
    backgroundColor: "#28a745",
    color: "white",
    fontSize: "16px",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};
