import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { getCSRFToken } from '../auth/AuthUtils';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [profile, setProfile] = useState(null); // ‚úÖ Store user details
  const [addresses, setAddresses] = useState([]); // ‚úÖ Store addresses from API
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [editingAddress, setEditingAddress] = useState(null); // ‚úÖ Store editing address
  const navigate = useNavigate();

  useEffect(() => {
    loadCartItems();
    fetchProfile(); // ‚úÖ Fetch user details and addresses
  }, []);

  // ‚úÖ Load Cart Items from Local Storage
  const loadCartItems = () => {
    const storedCartItems = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(storedCartItems);
    const total = storedCartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalPrice(total);
  };

  // ‚úÖ Fetch User Profile and Addresses
  const fetchProfile = async () => {
    console.log("üîÑ Fetching profile...");
    try {
      const csrfToken = await getCSRFToken();
      const authToken = localStorage.getItem("authToken");

      const response = await axios.get("http://127.0.0.1:8000/api/auth/profile/", {
        withCredentials: true,
        headers: {
          "X-CSRFToken": csrfToken,
          "Authorization": authToken ? `Token ${authToken}` : "",
          "Content-Type": "application/json",
        },
      });

      console.log("‚úÖ Profile Data:", response.data);

      // ‚úÖ Store profile data (excluding orders)
      setProfile({
        name: response.data.name,
        email: response.data.email,
        phone: response.data.phone_number,
      });

      // ‚úÖ Store addresses
      setAddresses(response.data.addresses || []);
      if (response.data.addresses.length > 0) {
        setSelectedAddress(response.data.addresses[0].id);
      }
    } catch (error) {
      console.error("üö® Error fetching profile:", error.response?.data || error);
      alert("Error loading profile. Please check authentication.");
      setProfile(null);
      setAddresses([]); // Ensure addresses is an array to avoid `.map()` errors
    }
  };

  // ‚úÖ Handle Address Selection
  const handleAddressChange = (e) => {
    setSelectedAddress(e.target.value);
  };

  // ‚úÖ Enable Address Editing
  const startEditing = (address) => {
    setEditingAddress({ ...address });
  };

  // ‚úÖ Handle Address Input Change
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditingAddress((prev) => ({ ...prev, [name]: value }));
  };

  const saveEditedAddress = async () => {
    if (!editingAddress) return;

    try {
      const csrfToken = await getCSRFToken();
      const authToken = localStorage.getItem("authToken");

      const response = await axios.put(
        `http://127.0.0.1:8000/api/auth/update-address/${editingAddress.id}/`,
        editingAddress,
        {
          headers: {
            "X-CSRFToken": csrfToken,
            "Authorization": authToken ? `Token ${authToken}` : "",
            "Content-Type": "application/json",
          },
          withCredentials: true,
        }
      );

      console.log("‚úÖ Address Updated:", response.data);

      // ‚úÖ Update address in frontend state after successful API update
      const updatedAddresses = addresses.map((addr) =>
        addr.id === editingAddress.id ? editingAddress : addr
      );
      setAddresses(updatedAddresses);

      setEditingAddress(null);
      alert("‚úÖ Address updated successfully!");
    } catch (error) {
      console.error("üö® Error updating address:", error.response?.data || error);
      alert("‚ùå Failed to update address. Try again.");
    }
  };

  return (
    <div>
      <h1>Your Cart</h1>

      {/* ‚úÖ User Details (Without Orders) */}
      {profile ? (
        <div>
          <p><strong>Name:</strong> {profile.name}</p>
          <p><strong>Email:</strong> {profile.email}</p>
          <p><strong>Phone:</strong> {profile.phone}</p>
        </div>
      ) : (
        <p>Loading user details...</p>
      )}

      {/* ‚úÖ Cart Items */}
      {cartItems.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <div>
          {cartItems.map((item, index) => (
            <div key={index} className="cart-item" style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
              <img src={item.image || '/images/products/default.jpg'} alt={item.name || 'Product'} width="100" height="100" style={{ marginRight: '20px' }} />
              <div style={{ flexGrow: 1 }}>
                <h3>{item.name || `Product ${item.id}`}</h3>
                <p>Price: ‚Çπ{item.price}</p>
                <p>Total: ‚Çπ{(item.price * item.quantity).toFixed(2)}</p>
              </div>
            </div>
          ))}

          <p><strong>Total Price: ‚Çπ{totalPrice.toFixed(2)}</strong></p>
          <button onClick={() => navigate(`/checkout?total_price=${totalPrice}&address_id=${selectedAddress}`)} className="btn btn-success">Checkout</button>
        </div>
      )}

      {/* ‚úÖ Address Selection */}
      <h2>Select Address for Delivery</h2>
      {addresses.length > 0 ? (
        <select value={selectedAddress || ''} onChange={handleAddressChange} id="address-select">
          {addresses.map((address) => (
            <option key={address.id} value={address.id}>
              {address.address_line1}, {address.city}, {address.state} - {address.postal_code}
            </option>
          ))}
        </select>
      ) : (
        <p>No saved addresses. <button onClick={() => navigate("/add-address")}>Add Address</button></p>
      )}

      {/* ‚úÖ Address Editing Section */}
      <h2>Edit Address</h2>
      {editingAddress ? (
        <div>
          <input
            type="text"
            name="address_line1"
            placeholder="Address Line 1"
            value={editingAddress.address_line1}
            onChange={handleEditChange}
          />
          <input
            type="text"
            name="city"
            placeholder="City"
            value={editingAddress.city}
            onChange={handleEditChange}
          />
          <input
            type="text"
            name="state"
            placeholder="State"
            value={editingAddress.state}
            onChange={handleEditChange}
          />
          <input
            type="text"
            name="postal_code"
            placeholder="Postal Code"
            value={editingAddress.postal_code}
            onChange={handleEditChange}
          />
          <button onClick={saveEditedAddress}>Save Changes</button>
          <button onClick={() => setEditingAddress(null)}>Cancel</button>
        </div>
      ) : (
        addresses.length > 0 && (
          <button onClick={() => startEditing(addresses[0])}>Edit Address</button>
        )
      )}
    </div>
  );
};

export default Cart;
