import React, { useEffect, useState } from 'react';
import Layout from '../components/Layout';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Checkout = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [userInfo, setUserInfo] = useState({
    name: 'John Doe',
    phone: '9876543210',
    email: 'johndoe@example.com',
  });

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Fetch cart items from localStorage
    const storedCartItems = JSON.parse(localStorage.getItem('cart')) || [];
    setCartItems(storedCartItems);

    // Calculate total price
    const total = storedCartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);
    setTotalPrice(total);

    // Retrieve address ID from URL parameters
    const queryParams = new URLSearchParams(location.search);
    const addressId = queryParams.get('address_id');

    // Fetch addresses from localStorage
    const storedAddresses = JSON.parse(localStorage.getItem('addresses')) || [];
    const selectedAddr = storedAddresses.find(addr => addr.id.toString() === addressId);
    setSelectedAddress(selectedAddr);

  }, [location]);

  // Handle final order submission
  const handlePlaceOrder = () => {
    alert('Order placed successfully!');
    localStorage.removeItem('cart');  // Clear the cart after placing the order
    navigate('/');  // Redirect to homepage after successful order
  };

  const handlePayment = async () => {
    try {
      // Step 1: Create Razorpay order from Django backend
      const response = await axios.post('/api/create_razorpay_order/', { amount: totalPrice });
      const { order_id, key, amount } = response.data;

      // Step 2: Open Razorpay payment window
      const options = {
        key: key,
        amount: amount,
        currency: 'INR',
        name: 'Harit Aahar',
        description: 'Payment for your order',
        order_id: order_id,
        handler: async function (response) {
          const { razorpay_payment_id, razorpay_order_id, razorpay_signature } = response;

          // Step 3: Verify payment with Django backend
          try {
            const verifyResponse = await axios.post('/api/verify_payment/', {
              razorpay_payment_id,
              razorpay_order_id,
              razorpay_signature,
            });

            if (verifyResponse.data.status === 'success') {
              alert('Payment successful and verified!');
              localStorage.removeItem('cart');  // Clear the cart after successful payment
              navigate('/');  // Redirect to homepage
            } else {
              alert('Payment verification failed. Please contact support.');
            }
          } catch (error) {
            console.error('Error verifying payment:', error);
            alert('An error occurred during payment verification.');
          }
        },
        prefill: {
          name: userInfo.name,
          email: userInfo.email,
          contact: userInfo.phone,
        },
        notes: {
          address: selectedAddress ? `${selectedAddress.address_line1}, ${selectedAddress.city}` : '',
        },
        theme: {
          color: '#28a745',
        },
      };

      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      console.error('Error during payment:', error);
      alert('Payment initiation failed. Please try again.');
    }
  };


  return (
    <div>
      <h1>Checkout</h1>

      {/* Display User Information */}
      <div style={styles.section}>
        <h2>User Information</h2>
        <p><strong>Name:</strong> {userInfo.name}</p>
        <p><strong>Phone:</strong> {userInfo.phone}</p>
        <p><strong>Email:</strong> {userInfo.email}</p>
      </div>

      {/* Display Address Information */}
      <div style={styles.section}>
        <h2>Delivery Address</h2>
        {selectedAddress ? (
          <div>
            <p><strong>{selectedAddress.name}</strong></p>
            <p>{selectedAddress.address_line1}</p>
            <p>{selectedAddress.city} - {selectedAddress.postal_code}</p>
          </div>
        ) : (
          <p>No address selected. <a href="/cart">Go back to cart</a> and select an address.</p>
        )}
      </div>

      {/* Display Cart Items */}
      <div style={styles.section}>
        <h2>Order Summary</h2>
        {cartItems.length > 0 ? (
          <ul style={styles.cartList}>
            {cartItems.map((item, index) => (
              <li key={index} style={styles.cartItem}>
                <img src={item.image} alt={item.name} style={styles.productImage} />
                <div>
                  <p><strong>{item.name}</strong></p>
                  <p>Price: ₹{item.price}</p>
                  <p>Quantity: {item.quantity}</p>
                  <p>Total: ₹{(item.price * item.quantity).toFixed(2)}</p>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>

      {/* Total Price and Place Order Button */}
      <div style={styles.section}>
        <h2>Total Price: ₹{totalPrice.toFixed(2)}</h2>
        <button onClick={handlePayment} style={styles.placeOrderButton}>Place Order</button>
      </div>
    </div>
  );
};

export default Checkout;

const styles = {
  section: {
    marginBottom: '30px',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    backgroundColor: '#f9f9f9',
  },
  cartList: {
    listStyle: 'none',
    padding: 0,
  },
  cartItem: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '15px',
  },
  productImage: {
    width: '80px',
    height: '80px',
    objectFit: 'cover',
    marginRight: '20px',
    borderRadius: '5px',
  },
  placeOrderButton: {
    padding: '12px 20px',
    backgroundColor: '#28a745',
    color: 'white',
    fontSize: '16px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};
