import React, { useEffect, useState } from "react";




// Function to import all images dynamically
const importAll = (requireContext) => {
  const images = requireContext.keys().map(requireContext);
  console.log("✅ Imported Images:", images);  // ✅ Log imported images
  return images;
};


// Import images for slides and categories
const slideImages = importAll(require.context("../assets/images/slides", false, /\.(png|jpe?g|jpeg|svg)$/));
const categoryImages = importAll(require.context("../assets/images/categories", false, /\.(png|jpe?g|jpeg|svg)$/));

const Home = () => {
  const [slides, setSlides] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [products, setProducts] = useState([]);

    // Fetch categories from backend
    useEffect(() => {
        fetch("http://127.0.0.1:8000/api/categories/")
            .then(response => response.json())
            .then(data => {
                setCategories(data.categories);
            })
            .catch(error => console.error("❌ Error fetching categories:", error));
    }, []);

    // Fetch products only when category is selected
    useEffect(() => {
        if (selectedCategory !== null) {
            fetch(`http://127.0.0.1:8000/api/products/${selectedCategory}/`)
                .then(response => response.json())
                .then(data => {
                    setProducts(data.products);
                })
                .catch(error => console.error("❌ Error fetching products:", error));
        }
    }, [selectedCategory]);

    return (
        <div className="content-wrapper">
            {/* Hero Section */}
            <section id="hero">
                <div id="heroCarousel" className="carousel slide" data-bs-ride="carousel" data-bs-interval="2000">
                    <div className="carousel-inner">
                        {Object.values(slideImages).map((image, index) => (
                            <div className={`carousel-item ${index === 0 ? "active" : ""}`} key={index}>
                                <img
                                    src={image.default || image}
                                    className="d-block w-100"
                                    alt={`Slide ${index + 1}`}
                                    style={{ maxHeight: "400px", objectFit: "cover" }}
                                />
                                <div className="carousel-caption d-none d-md-block">
                                    <h1>Revolutionizing the Green Industry</h1>
                                    <p>We deliver the freshest, eco-friendly food at your doorstep while nurturing the planet.</p>
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Carousel Controls */}
                    <button className="carousel-control-prev" type="button" data-bs-target="#heroCarousel" data-bs-slide="prev">
                        <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Previous</span>
                    </button>
                    <button className="carousel-control-next" type="button" data-bs-target="#heroCarousel" data-bs-slide="next">
                        <span className="carousel-control-next-icon" aria-hidden="true"></span>
                        <span className="visually-hidden">Next</span>
                    </button>
                </div>
            </section>

            {/* Categories Section */}
            {/* Categories Section */}
<section id="categories" style={{ marginTop: "50px" }}>
    <div className="container">
        <h2>Our Product Categories</h2>

        {loading ? (
            <p>Loading categories...</p>  // ✅ Show a message while loading
        ) : categories.length === 0 ? (
            <p>No categories available.</p>  // ✅ Handle empty response gracefully
        ) : (
            <div className="category-container" style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
                {categories.map((category) => {
                    const categoryImage = categoryImages[category.name.toLowerCase()] || null;
                    return (
                        <div
                            key={category.id}
                            className="category-box"
                            onClick={() => setSelectedCategory(category.id)}
                            style={{
                                cursor: "pointer",
                                textAlign: "center",
                                padding: "10px",
                                border: selectedCategory === category.id ? "2px solid green" : "1px solid gray",
                            }}
                        >
                            {categoryImage ? (
                                <img
                                    src={categoryImage.default || categoryImage}
                                    alt={category.name}
                                    className="category-image"
                                    style={{
                                        width: "150px",
                                        height: "150px",
                                        objectFit: "cover",
                                        borderRadius: "10px",
                                    }}
                                />
                            ) : (
                                <p>No Image</p>
                            )}
                            <p>{category.name}</p>
                        </div>
                    );
                })}
            </div>
        )}
    </div>
</section>


            {/* Products Section - Show Only When a Category is Selected */}
            {selectedCategory !== null && (
                <section id="products" style={{ marginTop: "50px" }}>
                    <div className="container">
                        <h2>Products</h2>
                        <div className="product-container" style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
                            {products.length > 0 ? (
                                products.map((product) => (
                                    <div key={product.id} className="product-box" style={{ textAlign: "center", padding: "10px", border: "1px solid gray" }}>
                                        <img
                                            src={product.image}
                                            alt={product.name}
                                            style={{ width: "150px", height: "150px", objectFit: "cover", borderRadius: "10px" }}
                                        />
                                        <p>{product.name}</p>
                                        <p>₹{product.price}</p>
                                    </div>
                                ))
                            ) : (
                                <p>No products found for this category.</p>
                            )}
                        </div>
                    </div>
                </section>
            )}
        </div>
    );
};

export default Home;
