import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getCSRFToken, sendOTP, verifyOTP } from "../auth/AuthUtils";
axios.defaults.withCredentials = true;


const Login = () => {
  // Using "phone" since this is a phone-based login system.
  const [userInfo, setUserInfo] = useState({ phone: "", password: "" });
  const [otp, setOTP] = useState("");
  // otpStage: "none" = no OTP sent, "sent" = OTP sent but not verified, "verified" = OTP verified
  const [otpStage, setOtpStage] = useState("none");
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo((prev) => ({ ...prev, [name]: value }));
  };

  // Step 1: Check credentials using the login endpoint.
  // Only if credentials are valid, proceed to send OTP.
  const handleCheckCredentials = async () => {
    if (!userInfo.phone || !userInfo.password) {
      alert("Please enter both phone number and password.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      if (!csrfToken) {
        alert("CSRF Token missing. Refresh the page and try again.");
        return;
      }
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login/",
        {
          username: userInfo.phone, // Backend expects "username" (in this case, the phone number)
          password: userInfo.password,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        // Credentials are valid. Now send the OTP.
        await handleSendOTP();
      } else {
        alert("Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("Credentials check error:", error.response?.data || error);
      alert("Error checking credentials.");
    }
  };

  // Step 2: Send OTP to the phone number.
  const handleSendOTP = async () => {
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/send-otp/",
        { phone_number: userInfo.phone },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        alert("✅ OTP sent successfully!");
        setOtpStage("sent");
      } else {
        alert("❌ Failed to send OTP: " + response.data.message);
      }
    } catch (error) {
      console.error("Error sending OTP:", error.response?.data || error);
      alert("Error sending OTP.");
    }
  };

  // Step 3: Verify the OTP entered by the user.
  const handleVerifyOTP = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }
    try {
      const response = await verifyOTP(userInfo.phone, otp);
      if (response.success) {
        alert("✅ OTP verified successfully!");
        setOtpStage("verified");
      } else {
        alert("❌ Invalid OTP.");
      }
    } catch (error) {
      console.error("Error verifying OTP:", error.response?.data || error);
      alert("Error verifying OTP.");
    }
  };

  // Step 4: Finalize login using an OTP-based endpoint.
  const handleOTPLogin = async () => {
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login-otp/",
        {
          phone: userInfo.phone,
          password: userInfo.password,
          otp: otp,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        const userData = {
          name: response.data.user.name,
          phone: response.data.user.phone_number,
        };
        localStorage.setItem("authToken", response.data.authToken);
        localStorage.setItem("user", JSON.stringify(userData));
        window.dispatchEvent(new Event("storage"));
        alert("✅ Login successful!");
        navigate("/profile");
      } else {
        alert("❌ Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("OTP Login error:", error.response?.data || error);
      alert("Login failed. Check console for details.");
    }
  };

  // The button handler drives the flow:
  // - "none": Check credentials and send OTP.
  // - "sent": Verify OTP.
  // - "verified": Finalize login.
  const handleButtonClick = async () => {
    if (otpStage === "none") {
      await handleCheckCredentials();
    } else if (otpStage === "sent") {
      await handleVerifyOTP();
    } else if (otpStage === "verified") {
      await handleOTPLogin();
    }
  };

  // Button text changes based on the current OTP stage.
  let buttonText = "";
  if (otpStage === "none") {
    buttonText = "Send OTP";
  } else if (otpStage === "sent") {
    buttonText = "Verify OTP";
  } else if (otpStage === "verified") {
    buttonText = "Login";
  }

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        name="phone"
        placeholder="Enter your phone number"
        value={userInfo.phone}
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Enter your password"
        value={userInfo.password}
        onChange={handleChange}
      />
      {otpStage !== "none" && (
        <input
          type="text"
          placeholder="Enter OTP"
          value={otp}
          onChange={(e) => setOTP(e.target.value)}
        />
      )}
      <button onClick={handleButtonClick}>{buttonText}</button>
      <button onClick={() => navigate("/signup")}>Signup</button>
      <button onClick={() => navigate("/forgot-password")}>Forgot Password?</button>
    </div>
  );
};

export default Login;
