import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    console.log("üîÑ Fetching profile...");

    try {
      // Get CSRF Token (Required for authenticated requests)
      const csrfResponse = await axios.get("http://127.0.0.1:8000/api/auth/csrf/", {
        withCredentials: true, // ‚úÖ Ensures CSRF cookies are stored
      });

      const csrfToken = csrfResponse.data.csrfToken;
      console.log("‚úÖ CSRF Token:", csrfToken);

      // Fetch the user profile
      const response = await axios.get("http://127.0.0.1:8000/api/auth/profile/", {
        headers: {
          "X-CSRFToken": csrfToken,
        },
        withCredentials: true, // ‚úÖ Ensures session cookies are sent
      });

      console.log("‚úÖ Profile fetched:", response.data);
      setUser(response.data);
      setOrders(response.data.orders || []);
    } catch (error) {
      console.error("üö® Error fetching profile:", error.response?.data || error);
      alert("‚ùå Failed to load profile. Please log in again.");
    }
  };

  return (
    <div>
      <h1>Profile</h1>
      {user ? (
        <div>
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone_number}</p>

          <h2>Orders</h2>
          {orders.length > 0 ? (
            <ul>
              {orders.map((order) => (
                <li key={order.id}>
                  <p>Order ID: {order.id}</p>
                  <p>Status: {order.status}</p>
                  <p>Total: ‚Çπ{order.total_amount}</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No orders found.</p>
          )}
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
    </div>
  );
};

export default Profile;
