import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { getCSRFToken } from '../auth/AuthUtils'; // Ensure correct import

const Login = () => {
  const navigate = useNavigate();

  // ‚úÖ Define userInfo state
  const [userInfo, setUserInfo] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  const handleLogin = async () => {
    console.log('üöÄ Attempting login...');

    const csrfToken = await getCSRFToken();  // ‚úÖ Fetch CSRF before login
    console.log('‚úÖ CSRF Token:', csrfToken);

    if (!csrfToken) {
      alert('‚ùå CSRF Token missing. Refresh and try again.');
      return;
    }

    try {
      const response = await axios.post(
        'http://127.0.0.1:8000/api/auth/login/',
        {
          username: userInfo.email,  // ‚úÖ Email or phone should match backend
          password: userInfo.password,
        },
        {
          headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': csrfToken,  // ‚úÖ Send CSRF token
          },
          withCredentials: true,  // ‚úÖ Allow Django session auth
        }
      );

      console.log('üéâ Login Success:', response.data);
      if (response.data.success) {
        alert('‚úÖ Login successful!');
        navigate('/profile');
      } else {
        alert('‚ùå Login failed: ' + response.data.message);
      }
    } catch (error) {
      console.error('üî• Login Error:', error.response?.data || error);
      alert('‚ùå Login failed. Check console for details.');
    }
  };














  return (
    <div className="login-container">
      <h2>Login</h2>
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={userInfo.email}
        onChange={handleChange}
        required
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={userInfo.password}
        onChange={handleChange}
        required
      />
      <button onClick={handleLogin}>Login</button>
      <p>Don't have an account? <a href="/signup">Sign up</a></p>
      <p>Forgot password? <a href="/forgot-password">Reset here</a></p>
    </div>
  );
};

export default Login;
