import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getCSRFToken, sendOTP, verifyOTP } from "../auth/AuthUtils";

const Login = () => {
  const [userInfo, setUserInfo] = useState({ email: "", password: "" });
  const [otp, setOTP] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [verified, setVerified] = useState(false);
  const navigate = useNavigate();

  // If the identifier is only digits, treat it as a phone number
  const isPhoneLogin = /^\d+$/.test(userInfo.email);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  // Email login flow
  const handleEmailLogin = async () => {
    if (!userInfo.email || !userInfo.password) {
      alert("âŒ Please enter both email and password.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      if (!csrfToken) {
        alert("âŒ CSRF Token missing. Refresh the page and try again.");
        return;
      }
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login/",
        {
          username: userInfo.email,
          password: userInfo.password,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        const userData = {
          name: response.data.user.name,
          email: response.data.user.email,
          phone_number: response.data.user.phone_number,
        };
        localStorage.setItem("authToken", response.data.authToken);
        localStorage.setItem("user", JSON.stringify(userData));
        window.dispatchEvent(new Event("storage"));
        alert("âœ… Login successful!");
        navigate("/profile");
      } else {
        alert("âŒ Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("ðŸ”¥ Login Error:", error.response?.data || error);
      alert("âŒ Login failed. Check console for details.");
    }
  };

  // OTP flow functions for phone login
  const handleSendOTP = async () => {
    if (!userInfo.email) {
      alert("âŒ Please enter your phone number.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/send-otp/",
        { phone_number: userInfo.email },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        alert("âœ… OTP sent successfully!");
        setOtpSent(true);
      } else {
        alert("âŒ Failed to send OTP: " + response.data.message);
      }
    } catch (error) {
      console.error("ðŸ”¥ Error sending OTP:", error.response?.data || error);
      alert("âŒ Error sending OTP. Check console for details.");
    }
  };

  const handleVerifyOTP = async () => {
    if (!otp) {
      alert("âŒ Please enter the OTP.");
      return;
    }
    try {
      const response = await verifyOTP(userInfo.email, otp);
      if (response.success) {
        alert("âœ… OTP verified successfully!");
        setVerified(true);
      } else {
        alert("âŒ Invalid OTP.");
      }
    } catch (error) {
      console.error("ðŸ”¥ Error verifying OTP:", error);
      alert("âŒ Error verifying OTP.");
    }
  };

  // Complete phone login after OTP verification
  const handlePhoneLogin = async () => {
    if (!userInfo.email || !userInfo.password) {
      alert("âŒ Please enter both phone number and password.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login-otp/",
        {
          phone: userInfo.email,
          password: userInfo.password,
          otp: otp,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        const userData = {
          name: response.data.user.name,
          email: response.data.user.email,
          phone_number: response.data.user.phone_number,
        };
        localStorage.setItem("authToken", response.data.authToken);
        localStorage.setItem("user", JSON.stringify(userData));
        window.dispatchEvent(new Event("storage"));
        alert("âœ… Login successful!");
        navigate("/profile");
      } else {
        alert("âŒ Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("ðŸ”¥ Phone Login Error:", error.response?.data || error);
      alert("âŒ Login failed. Check console for details.");
    }
  };

  // This button handler drives the OTP flow for phone login:
  // - If OTP not yet sent, it sends OTP.
  // - If OTP sent but not verified, it verifies OTP.
  // - If OTP is verified, it completes the login.
  const handleButtonClick = async () => {
    if (isPhoneLogin) {
      if (!otpSent) {
        await handleSendOTP();
      } else if (!verified) {
        await handleVerifyOTP();
      } else {
        await handlePhoneLogin();
      }
    } else {
      // Email login flow
      await handleEmailLogin();
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input
        type="text"
        name="email"
        placeholder="Phone (for OTP) or Email"
        value={userInfo.email}
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={userInfo.password}
        onChange={handleChange}
      />
      {isPhoneLogin && otpSent && !verified && (
        <input
          type="text"
          placeholder="Enter OTP"
          value={otp}
          onChange={(e) => setOTP(e.target.value)}
        />
      )}
      <button onClick={handleButtonClick}>
        {isPhoneLogin
          ? !otpSent
            ? "Send OTP"
            : !verified
            ? "Verify OTP"
            : "Login"
          : "Login"}
      </button>
      <button onClick={() => navigate("/signup")}>Signup</button>
      <button onClick={() => navigate("/forgot-password")}>
        Forgot Password?
      </button>
    </div>
  );
};

export default Login;
