import axios from 'axios';
const api = axios.create({
    baseURL: 'http://127.0.0.1:8000', // Or your production URL
    withCredentials: true, // Important: Include for CSRF
  });
// ✅ Function to fetch CSRF token
export const getCSRFToken = async () => {
  try {
    const response = await axios.get('/api/auth/csrf/', { withCredentials: true });
    return response.data.csrfToken;
  } catch (error) {
    console.error('Error fetching CSRF token:', error);
    return null;
  }
};

// ✅ Function to send OTP
export const sendOTP = async (phoneNumber) => {
  try {
    const csrfToken = await getCSRFToken(); // Fetch CSRF Token before sending request

    const response = await axios.post(
      '/api/auth/send-otp/',
      { phone_number: phoneNumber },
      {
        headers: {
          'X-CSRFToken': csrfToken,  // Send CSRF Token in header
          'Content-Type': 'application/json',
        },
        withCredentials: true,  // Allow cookies for CSRF
      }
    );

    return response.data;
  } catch (error) {
    console.error('Error sending OTP:', error.response || error);
    throw error;
  }
};



// Function to verify OTP
export const verifyOTP = async (phoneNumber, otp) => {
  try {
    const response = await axios.post('/api/auth/verify-otp/', { phone_number: phoneNumber, otp });
    return response.data;
  } catch (error) {
    console.error('Error verifying OTP:', error);
    throw error;
  }
};


// Signup Function
export const signupUser = async (userInfo) => {
  try {
    const csrfToken = await getCSRFToken();
    if (!csrfToken) {
      throw new Error('CSRF Token missing. Try reloading the page.');
    }

    console.log('Sending signup request with:', userInfo);  // Log data before request

    const response = await axios.post(
      '/api/auth/signup/',
      userInfo,
      {
        headers: {
          'X-CSRFToken': csrfToken,
        },
        withCredentials: true,
      }
    );

    console.log('Signup API response:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error during signup:', error.response?.data || error);
    throw error;
  }
};
