import axios from 'axios';

// ✅ Function to fetch CSRF token
export const getCSRFToken = async () => {
  try {
    const response = await axios.get('http://127.0.0.1:8000/api/auth/csrf/', { withCredentials: true });
    return response.data.csrfToken;
  } catch (error) {
    console.error('Error fetching CSRF token:', error);
    return null;
  }
};

// ✅ Function to send OTP
export const sendOTP = async (phoneNumber) => {
  try {
    const csrfToken = await getCSRFToken(); // Fetch CSRF Token before sending request

    const response = await axios.post(
      'http://127.0.0.1:8000/api/auth/send_otp/',
      { phone_number: phoneNumber },
      {
        headers: {
          'X-CSRFToken': csrfToken,  // Send CSRF Token in header
          'Content-Type': 'application/json',
        },
        withCredentials: true,  // Allow cookies for CSRF
      }
    );

    return response.data;
  } catch (error) {
    console.error('Error sending OTP:', error.response || error);
    throw error;
  }
};



// Function to verify OTP
export const verifyOTP = async (phoneNumber, otp) => {
  try {
    const response = await axios.post('/api/auth/verify_otp/', { phone_number: phoneNumber, otp });
    return response.data;
  } catch (error) {
    console.error('Error verifying OTP:', error);
    throw error;
  }
};
