import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getCSRFToken } from "../auth/AuthUtils"; // ‚úÖ Ensure this is imported

const Login = () => {
  const [userInfo, setUserInfo] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  const handleLogin = async () => {
    console.log("üöÄ Attempting login...");
    console.log("üìå Sending Login Data:", userInfo); // ‚úÖ Debugging

    if (!userInfo.email || !userInfo.password) {
      alert("‚ùå Please enter both email/phone and password.");
      return;
    }

    try {
      const csrfToken = await getCSRFToken(); // ‚úÖ Fetch CSRF token first
      console.log("‚úÖ CSRF Token:", csrfToken);

      if (!csrfToken) {
        alert("‚ùå CSRF Token missing. Refresh the page and try again.");
        return;
      }

      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login/",
        {
          username: userInfo.email, // ‚úÖ Backend expects "username"
          password: userInfo.password,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken, // ‚úÖ Send CSRF token
          },
          withCredentials: true, // ‚úÖ Allows session cookies
        }
      );

      console.log("üéâ Login Success:", response.data);
      if (response.data.success) {
        const userData = {
          name: response.data.user.name,
          email: response.data.user.email,
          phone_number: response.data.user.phone_number,
        };
        localStorage.setItem("authToken", response.data.authToken);
        alert("‚úÖ Login successful!");
        localStorage.setItem("user", JSON.stringify(userData)); // ‚úÖ Store full user data
      window.dispatchEvent(new Event("storage")); // ‚úÖ Notify Layout.js of login


        navigate("/profile"); // Redirect to profile page
      } else {
        alert("‚ùå Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("üî• Login Error:", error.response?.data || error);
      alert("‚ùå Login failed. Check console for details.");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input type="text" name="email" placeholder="Email or Phone" value={userInfo.email} onChange={handleChange} />
      <input type="password" name="password" placeholder="Password" value={userInfo.password} onChange={handleChange} />
      <button onClick={handleLogin}>Login</button>
      <button onClick={() => navigate('/signup')}>Signup</button>
      <button onClick={() => navigate('/forgot-password')}>Forgot Password?</button>

    </div>
  );
};

export default Login;
