import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/auth/profile/', {
          headers: {
            'Content-Type': 'application/json',
          },
          withCredentials: true, // ‚úÖ Ensures session cookies are sent
        });

        console.log('‚úÖ Profile Data:', response.data);
        setUser(response.data);
        setOrders(response.data.orders || []);
      } catch (error) {
        console.error('üö® Error fetching profile:', error.response?.data || error);
        alert('‚ùå Failed to load profile. Redirecting to login.');
        navigate('/login'); // ‚úÖ Redirect if not authenticated
      }
    };

    fetchProfile();
  }, [navigate]);

  if (!user) {
    return <h2>Loading profile...</h2>; // ‚úÖ Prevents .map() on undefined
  }

  return (
    <div className="profile-container">
      <h1>Profile</h1>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Phone:</strong> {user.phone_number}</p>

      <h2>Order History</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <ul>
          {orders.map((order, index) => (
            <li key={index}>
              <p><strong>Order ID:</strong> {order.id}</p>
              <p><strong>Total:</strong> ‚Çπ{order.total_amount}</p>
              <p><strong>Status:</strong> {order.status}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Profile;
