import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { getCSRFToken } from '../auth/AuthUtils';  // Import CSRF helper

const Login = () => {
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
  };

  const handleLogin = async () => {
    console.log('üöÄ Attempting login...');
    const csrfToken = getCSRFToken();

    try {
      const response = await axios.post(
        'http://127.0.0.1:8000/api/auth/login/', 
        loginData,
        {
          headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': csrfToken,  // Include CSRF token
          },
          withCredentials: true,  // Ensure cookies are sent
        }
      );

      if (response.data.success) {
        alert('‚úÖ Login successful!');
        navigate('/profile');  // Redirect to profile page
      } else {
        alert('‚ùå Invalid credentials');
      }
    } catch (error) {
      console.error('üî• Login failed:', error.response?.data || error);
      alert('‚ùå Login failed. Check credentials and try again.');
    }
  };

  return (
    <div className="form-container">
      <h2>Login</h2>
      <input type="text" name="username" placeholder="Email or Phone" value={loginData.username} onChange={handleChange} required />
      <input type="password" name="password" placeholder="Password" value={loginData.password} onChange={handleChange} required />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
