import React, { useEffect, useState } from 'react';
import { Link, Outlet, useNavigate } from 'react-router-dom';
import '../assets/styles/style.css';
import logo from '../assets/images/LOGO.png';

const Layout = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('user')) || null);

  useEffect(() => {
    const checkUser = () => {
      const storedUser = JSON.parse(localStorage.getItem('user'));
      setUser(storedUser);
    };

    // âœ… Check user on initial load
    checkUser();

    // âœ… Listen for storage changes (e.g., login/logout events)
    window.addEventListener('storage', checkUser);

    return () => {
      window.removeEventListener('storage', checkUser);
    };
  }, [user]);

  const handleProfileClick = () => {
    console.log("ðŸ”¹ User State:", user); // âœ… Debugging

    if (user) {
      console.log("âœ… Redirecting to profile");
      navigate('/profile'); // Redirect to profile if logged in
    } else {
      console.log("âŒ Redirecting to login");
      navigate('/login'); // Redirect to login if not logged in
    }
  };
  const handleSearchSubmit = (e) => {
    e.preventDefault(); // Prevent traditional form submission
    if (searchQuery.trim() !== '') {
      navigate(`/products?search=${searchQuery}`); // Redirect to Products page with the search query
    }
  };
  const handleLogin = (userData) => {
    console.log("ðŸŸ¢ Storing user in localStorage:", userData);
    localStorage.setItem("user", JSON.stringify(userData)); // âœ… Store user in localStorage
    window.dispatchEvent(new Event("storage")); // âœ… Notify React state to update
    setUser(userData);
  };

  return (
    <>
      {/* Header */}
      <header>
        <div className="header-container">
          <div className="logo-container">
            <Link to="/">
              <img className="logo" src={logo} alt="Logo" />
            </Link>

            {/* âœ… Show User Name or "Please login" */}
            <span className="user-greeting">
              {user ? `Welcome, ${user.name}` : "Please login"}
            </span>
          </div>

          <nav>
            <ul className="nav-links">
              <li><Link to="/">Home</Link></li>
              <li><Link to="/products">Products</Link></li>
              <li><Link to="/about">About Us</Link></li>
              <li><Link to="/contact">Contact</Link></li>
            </ul>

            <div className="search-form-container">
              <form onSubmit={handleSearchSubmit}>
                <input
                  type="text"
                  name="query"
                  placeholder="Search for products..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
                <button type="submit">Search</button>
              </form>
            </div>
          </nav>

          <div className="header-right">
            {/* âœ… Always Show User Icon, Even if Not Logged In */}
            <button onClick={handleProfileClick} className="profile-icon-btn">
              <i className="fa fa-user" aria-hidden="true" style={{ fontSize: '30px' }}></i>
            </button>

            <Link to="/cart" className="cart-btn">
              <span className="cart-icon">
                <i className="fa fa-shopping-cart" style={{ fontSize: '24px' }}></i>
                <span id="cart-count">0</span>
              </span>
            </Link>
          </div>
        </div>
      </header>

      <main>
        <Outlet />
      </main>

      {/* Footer */}
      <footer>
        <p>&copy; 2025 Harit Aahar. All Rights Reserved.</p>
        <p>Designed to promote eco-friendly, sustainable food practices.</p>
      </footer>
    </>
  );
};

export default Layout;
