import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { getCSRFToken, sendOTP, verifyOTP } from "../auth/AuthUtils"; // Ensure these functions are imported

const Login = () => {
  const [userInfo, setUserInfo] = useState({ email: "", password: "" });
  const [otp, setOTP] = useState('');
  const [otpSent, setOtpSent] = useState(false);
  const [verified, setVerified] = useState(false);
  const navigate = useNavigate();

  // Determine if the entered identifier is a phone number (only digits)
  const isPhoneLogin = /^\d+$/.test(userInfo.email);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserInfo({ ...userInfo, [name]: value });
  };

  // Email/password login
  const handleLogin = async () => {
    if (!userInfo.email || !userInfo.password) {
      alert("‚ùå Please enter both email and password.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      if (!csrfToken) {
        alert("‚ùå CSRF Token missing. Refresh the page and try again.");
        return;
      }
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login/",
        {
          username: userInfo.email,
          password: userInfo.password,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        const userData = {
          name: response.data.user.name,
          email: response.data.user.email,
          phone_number: response.data.user.phone_number,
        };
        localStorage.setItem("authToken", response.data.authToken);
        localStorage.setItem("user", JSON.stringify(userData));
        window.dispatchEvent(new Event("storage"));
        alert("‚úÖ Login successful!");
        navigate("/profile");
      } else {
        alert("‚ùå Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("üî• Login Error:", error.response?.data || error);
      alert("‚ùå Login failed. Check console for details.");
    }
  };

  // OTP flow functions for phone login
  const handleSendOTP = async () => {
    if (!userInfo.email) {
      alert("Please enter your phone number.");
      return;
    }
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/send-otp/",
        { phone_number: userInfo.email },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        alert("‚úÖ OTP sent successfully!");
        setOtpSent(true);
      } else {
        alert("‚ùå Failed to send OTP: " + response.data.message);
      }
    } catch (error) {
      console.error("üî• Error sending OTP:", error.response?.data || error);
      alert("‚ùå Error sending OTP. Check console for details.");
    }
  };

  const handleVerifyOTP = async () => {
    if (!otp) {
      alert("Please enter the OTP.");
      return;
    }
    try {
      const response = await verifyOTP(userInfo.email, otp);
      if (response.success) {
        alert("‚úÖ OTP verified successfully!");
        setVerified(true);
      } else {
        alert("‚ùå Invalid OTP");
      }
    } catch (error) {
      alert("‚ùå Error verifying OTP");
    }
  };

  const handleOTPLogin = async () => {
    try {
      const csrfToken = await getCSRFToken();
      const response = await axios.post(
        "http://127.0.0.1:8000/api/auth/login-otp/",
        {
          phone: userInfo.email,
          otp: otp,
        },
        {
          headers: {
            "Content-Type": "application/json",
            "X-CSRFToken": csrfToken,
          },
          withCredentials: true,
        }
      );
      if (response.data.success) {
        const userData = {
          name: response.data.user.name,
          email: response.data.user.email,
          phone_number: response.data.user.phone_number,
        };
        localStorage.setItem("authToken", response.data.authToken);
        localStorage.setItem("user", JSON.stringify(userData));
        window.dispatchEvent(new Event("storage"));
        alert("‚úÖ Login successful!");
        navigate("/profile");
      } else {
        alert("‚ùå OTP Login failed: " + response.data.message);
      }
    } catch (error) {
      console.error("üî• OTP Login Error:", error.response?.data || error);
      alert("‚ùå OTP Login failed. Check console for details.");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <input 
        type="text" 
        name="email" 
        placeholder="Email or Phone" 
        value={userInfo.email} 
        onChange={handleChange} 
      />
      {isPhoneLogin ? (
        <>
          {!otpSent ? (
            <button onClick={handleSendOTP}>Send OTP</button>
          ) : (
            <>
              <input 
                type="text" 
                placeholder="Enter OTP" 
                value={otp} 
                onChange={(e) => setOTP(e.target.value)} 
              />
              {!verified ? (
                <button onClick={handleVerifyOTP}>Verify OTP</button>
              ) : (
                <button onClick={handleOTPLogin}>Login</button>
              )}
            </>
          )}
        </>
      ) : (
        <>
          <input 
            type="password" 
            name="password" 
            placeholder="Password" 
            value={userInfo.password} 
            onChange={handleChange} 
          />
          <button onClick={handleLogin}>Login</button>
        </>
      )}
      <button onClick={() => navigate('/signup')}>Signup</button>
      <button onClick={() => navigate('/forgot-password')}>Forgot Password?</button>
    </div>
  );
};

export default Login;
